(function ($) {
  $(document).ready(function () {

    $(window).scroll(function () {
      var scroll = $(window).scrollTop() + 100;
      var currentArea = $("section").filter(function () {
        return scroll <= $(this).offset().top + $(this).height();
      });

      if ($(window).scrollTop() > 100) {
        $('.header').addClass("headerScroll");

      } else if ($(window).scrollTop() < 100) {

        $('.header').removeClass("headerScroll");

      }
    });

    var tab = $('.tabs2__content');
    tab.hide().filter(':first').show();

    $('.tabs2__btn').click(function () {
      tab.hide();
      tab.filter(this.dataset.target).show();
      $('.tabs2__btn').removeClass('is-active');
      $(this).addClass('is-active');
      return false;
    }).filter(':first').click();


    $('.tab-link').click(function () {
      var id = $(this).attr('data-tab'),
        content = $('.tab-content[data-tab="' + id + '"]');

      $('.tab-link.active').removeClass('active');
      $(this).addClass('active');

      $('.tab-content.active').removeClass('active');
      content.addClass('active');
    });

    $('.tabs__nav').click(function (e) {
      e.preventDefault();
      if (!$(this).hasClass('is-active')) {
        $(this).toggleClass("is-active");
        $(this).parent().find(".tabs__content").slideToggle(300);
      } else {
        $(this).removeClass("is-active");
        $(this).parent().find(".tabs__content").slideUp(300);
      }
    });

    $(".callModal-1").fancybox({
      fixed: false,
      autoCenter: true,
      fitToView: false,
      autoSize: true,
      closeClick: false,
      autoFocus: false,
      openEffect: 'none',
      closeEffect: 'none'
    });
    $(".call-privacy-policy").fancybox({
      fixed: false,
      autoCenter: true,
      fitToView: false,
      autoSize: true,
      autoFocus: false,
      closeClick: false,
      openEffect: 'none',
      closeEffect: 'none'
    });

    $('input.phone').inputmask("+7 (999) 999-9999");


    $('input').on('focus', function () {
      if ($(this).hasClass('error')) {
        $(this).removeClass('error');
      }
    });


  });
})(jQuery);
